//编写store
import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const foodsStore = createSlice({
  name: 'food',
  initialState: {
    //商品列表
    foodsList: [],
    //菜单激活下标值
    activeIndex: 0,
    //购物车列表
    cartList: []
  },
  reducers: {
    //更改商品列表
    setFoodList(state, action) {
      state.foodsList = action.payload
    },
    //更改菜单激活下标值activeIndex
    changeActiveIndex(state, action) {
      state.activeIndex = action.payload
    },
    //添加购物车
    addCart(state,action){
      //是否添加过？以action.payload.id去cartList中匹配 匹配成功就添加过
      const item = state.cartList.find(item=>item.id === action.payload.id)
      if(item){
        //添加过，数量+1
        item.count++
      }else{
        state.cartList.push(action.payload)
      }
    },
    //删除购物车中对应新增的一件商品
    delCart(state,action){
      //以action.payload.id去cartList中匹配 匹配成功就删除
      const item = state.cartList.find(item=>item.id === action.payload.id)
    }
  }
})

//异步获取部分
const {setFoodList,changeActiveIndex,addCart,delCart}=foodsStore.actions
const fetchFoodList = ()=>{
  return async(dispatch)=>{
    //编写异步逻辑
    const res = await axios.get('http://localhost:3004/takeaway')
    //调用dispatch函数提交action
    dispatch(setFoodList(res.data))
  }
}

export {fetchFoodList,changeActiveIndex,addCart,delCart}

const foodsReducer = foodsStore.reducer
export default foodsReducer
